import matplotlib.pyplot as plt

# 将数据拆分为Ox, Oy, Oz, Ax, Ay, Az
Ox = data[0::6]
Oy = data[1::6]
Oz = data[2::6]
Ax = data[3::6]
Ay = data[4::6]
Az = data[5::6]

# 创建时间序列
time = range(len(Ox))

# 绘制曲线
plt.figure(figsize=(12, 8))

plt.subplot(2, 1, 1)
plt.plot(time, Ox, label='Ox')
plt.plot(time, Oy, label='Oy')
plt.plot(time, Oz, label='Oz')
plt.xlabel('Time')
plt.ylabel('O values')
plt.title('Ox, Oy, Oz over Time')
plt.legend()

plt.subplot(2, 1, 2)
plt.plot(time, Ax, label='Ax')
plt.plot(time, Ay, label='Ay')
plt.plot(time, Az, label='Az')
plt.xlabel('Time')
plt.ylabel('A values')
plt.title('Ax, Ay, Az over Time')
plt.legend()

plt.tight_layout()
plt.show()